<?php

namespace garethp\ews\API\Type;

use garethp\ews\API\Type;

/**
 * Class representing AppliedHashtagsPreviewType
 *
 *
 * XSD Type: AppliedHashtagsPreviewType
 */
class AppliedHashtagsPreviewType extends Type
{

    /**
     * @var string[]
     */
    protected $hashtags = null;

    /**
     * @autogenerated This method is safe to replace
     * @param $value string
     * @return AppliedHashtagsPreviewType
     */
    public function addHashtags($value)
    {
        if ($this->hashtags === null) {
                        $this->hashtags = array();
        }

        if (!is_array($this->hashtags)) {
            $this->hashtags = array($this->hashtags);
        }

        $this->hashtags[] = $value;
        return $this;
    }

    /**
     * @autogenerated This method is safe to replace
     * @return string[]
     */
    public function getHashtags()
    {
        return $this->hashtags;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value string[]|string
     * @return AppliedHashtagsPreviewType
     */
    public function setHashtags(array|string $value)
    {
        if (!is_array($value)) {
            $value = [$value];
        }
        $this->hashtags = $value;
        return $this;
    }
}
