<?php

namespace garethp\ews\API\Type\ConversationType;

use garethp\ews\API\Type\ConversationType;

/**
 * Class representing MailboxGuidsAType
 */
class MailboxGuidsAType extends ConversationType
{

    /**
     * @var string[]
     */
    protected $mailboxGuid = null;

    /**
     * @autogenerated This method is safe to replace
     * @param $value string
     * @return MailboxGuidsAType
     */
    public function addMailboxGuid($value)
    {
        if ($this->mailboxGuid === null) {
                        $this->mailboxGuid = array();
        }

        if (!is_array($this->mailboxGuid)) {
            $this->mailboxGuid = array($this->mailboxGuid);
        }

        $this->mailboxGuid[] = $value;
        return $this;
    }

    /**
     * @autogenerated This method is safe to replace
     * @return string[]
     */
    public function getMailboxGuid()
    {
        return $this->mailboxGuid;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value string[]|string
     * @return MailboxGuidsAType
     */
    public function setMailboxGuid(array|string $value)
    {
        if (!is_array($value)) {
            $value = [$value];
        }
        $this->mailboxGuid = $value;
        return $this;
    }
}
