<?php

namespace garethp\ews\API\Type;

use garethp\ews\API\Type;

/**
 * Class representing MailboxQueryType
 *
 * Pair of query and a set of mailbox search scopes.
 * XSD Type: MailboxQueryType
 */
class MailboxQueryType extends Type
{

    /**
     * @var string
     */
    protected $query = null;

    /**
     * @var \garethp\ews\API\Type\MailboxSearchScopeType[]
     */
    protected $mailboxSearchScopes = null;

    /**
     * @autogenerated This method is safe to replace
     * @return string
     */
    public function getQuery()
    {
        return $this->query;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value string
     * @return MailboxQueryType
     */
    public function setQuery($value)
    {
        $this->query = $value;
        return $this;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value MailboxSearchScopeType
     * @return MailboxQueryType
     */
    public function addMailboxSearchScopes(MailboxSearchScopeType $value)
    {
        if ($this->mailboxSearchScopes === null) {
                        $this->mailboxSearchScopes = array();
        }

        if (!is_array($this->mailboxSearchScopes)) {
            $this->mailboxSearchScopes = array($this->mailboxSearchScopes);
        }

        $this->mailboxSearchScopes[] = $value;
        return $this;
    }

    /**
     * @autogenerated This method is safe to replace
     * @return MailboxSearchScopeType[]
     */
    public function getMailboxSearchScopes()
    {
        return $this->mailboxSearchScopes;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value MailboxSearchScopeType[]|MailboxSearchScopeType
     * @return MailboxQueryType
     */
    public function setMailboxSearchScopes(array|MailboxSearchScopeType $value)
    {
        if (!is_array($value)) {
            $value = [$value];
        }
        $this->mailboxSearchScopes = $value;
        return $this;
    }
}
