<?php

namespace garethp\ews\API\Type\SearchResultsType;

/**
 * Class representing ConversationsAType
 */
class ConversationsAType
{

    /**
     * @var \garethp\ews\API\Type\ConversationType[]
     */
    protected $conversation = null;

    /**
     * @autogenerated This method is safe to replace
     * @param $value \garethp\ews\API\Type\ConversationType
     * @return ConversationsAType
     */
    public function addConversation(\garethp\ews\API\Type\ConversationType $value)
    {
        if ($this->conversation === null) {
                        $this->conversation = array();
        }

        if (!is_array($this->conversation)) {
            $this->conversation = array($this->conversation);
        }

        $this->conversation[] = $value;
        return $this;
    }

    /**
     * @autogenerated This method is safe to replace
     * @return \garethp\ews\API\Type\ConversationType[]
     */
    public function getConversation()
    {
        return $this->conversation;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value
     * \garethp\ews\API\Type\ConversationType[]|\garethp\ews\API\Type\ConversationType
     * @return ConversationsAType
     */
    public function setConversation(array|\garethp\ews\API\Type\ConversationType $value)
    {
        if (!is_array($value)) {
            $value = [$value];
        }
        $this->conversation = $value;
        return $this;
    }
}
