<?php

namespace garethp\ews\API\Type\SearchSuggestionsType;

/**
 * Class representing SuggestionsAType
 */
class SuggestionsAType
{

    /**
     * @var \garethp\ews\API\Type\SuggestionType[]
     */
    protected $suggestion = null;

    /**
     * @autogenerated This method is safe to replace
     * @param $value \garethp\ews\API\Type\SuggestionType
     * @return SuggestionsAType
     */
    public function addSuggestion(\garethp\ews\API\Type\SuggestionType $value)
    {
        if ($this->suggestion === null) {
                        $this->suggestion = array();
        }

        if (!is_array($this->suggestion)) {
            $this->suggestion = array($this->suggestion);
        }

        $this->suggestion[] = $value;
        return $this;
    }

    /**
     * @autogenerated This method is safe to replace
     * @return \garethp\ews\API\Type\SuggestionType[]
     */
    public function getSuggestion()
    {
        return $this->suggestion;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value
     * \garethp\ews\API\Type\SuggestionType[]|\garethp\ews\API\Type\SuggestionType
     * @return SuggestionsAType
     */
    public function setSuggestion(array|\garethp\ews\API\Type\SuggestionType $value)
    {
        if (!is_array($value)) {
            $value = [$value];
        }
        $this->suggestion = $value;
        return $this;
    }
}
