    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value string
     * @return TaskSuggestionType
     */
    public function setTaskString($value)
    {
        $this->taskString = $value;
        return $this;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value EmailUserType
     * @return TaskSuggestionType
     */
    public function addAssignees(EmailUserType $value)
    {
        if ($this->assignees === null) {
                        $this->assignees = array();
        }

        if (!is_array($this->assignees)) {
            $this->assignees = array($this->assignees);
        }

        $this->assignees[] = $value;
        return $this;
    }

    /**
     * @autogenerated This method is safe to replace
     * @return EmailUserType[]
     */
    public function getAssignees()
    {
        return $this->assignees;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value EmailUserType[]|EmailUserType
     * @return TaskSuggestionType
     */
    public function setAssignees(array|EmailUserType $value)
    {
        if (!is_array($value)) {
            $value = [$value];
        }
        $this->assignees = $value;
        return $this;
    }
}
