<?php

namespace garethp\ews\API\Type;

use garethp\ews\API\Type;

/**
 * Class representing TimeSlotType
 *
 *
 * XSD Type: TimeSlot
 */
class TimeSlotType extends Type
{

    /**
     * @var \DateTime
     */
    protected $startTime = null;

    /**
     * @var float
     */
    protected $durationInMinutes = null;

    /**
     * @autogenerated This method is safe to replace
     * @return \DateTime
     */
    public function getStartTime()
    {
        return $this->startTime;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value \DateTime|string
     * @return TimeSlotType
     */
    public function setStartTime(\DateTime|string $value)
    {
        if (is_string($value)) {
            $value = new \DateTime($value);
        }
        $this->startTime = $value;
        return $this;
    }

    /**
     * @autogenerated This method is safe to replace
     * @return float
     */
    public function getDurationInMinutes()
    {
        return $this->durationInMinutes;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value float
     * @return TimeSlotType
     */
    public function setDurationInMinutes($value)
    {
        $this->durationInMinutes = $value;
        return $this;
    }
}
