<?php

namespace garethp\ews\API\Type;

use garethp\ews\API\Type;

/**
 * Class representing WorkHoursType
 *
 *
 * XSD Type: WorkHoursType
 */
class WorkHoursType extends Type
{

    /**
     * @var string
     */
    protected $workDay = null;

    /**
     * @var \garethp\ews\API\Type\WorkTimeSlotType[]
     */
    protected $timeSlots = null;

    /**
     * @autogenerated This method is safe to replace
     * @return string
     */
    public function getWorkDay()
    {
        return $this->workDay;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value string
     * @return WorkHoursType
     */
    public function setWorkDay($value)
    {
        $this->workDay = $value;
        return $this;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value WorkTimeSlotType
     * @return WorkHoursType
     */
    public function addTimeSlots(WorkTimeSlotType $value)
    {
        if ($this->timeSlots === null) {
                        $this->timeSlots = array();
        }

        if (!is_array($this->timeSlots)) {
            $this->timeSlots = array($this->timeSlots);
        }

        $this->timeSlots[] = $value;
        return $this;
    }

    /**
     * @autogenerated This method is safe to replace
     * @return WorkTimeSlotType[]
     */
    public function getTimeSlots()
    {
        return $this->timeSlots;
    }

    /**
     * @autogenerated This method is safe to replace
     * @param $value WorkTimeSlotType[]|WorkTimeSlotType
     * @return WorkHoursType
     */
    public function setTimeSlots(array|WorkTimeSlotType $value)
    {
        if (!is_array($value)) {
            $value = [$value];
        }
        $this->timeSlots = $value;
        return $this;
    }
}
